mapper.select:
 		SELECT	id,auth 
		FROM	auth 
		WHERE	id=#{value}

mapper.update:
update member_size 
set shoulder=#{shoulder}, chest=#{chest}, sleeve=#{sleeve}, armhole=#{armhole}, toplength=#{toplength}, waist=#{waist}, crotch=#{crotch}, thigh=#{thigh}, hem=#{hem}, bottomlength=#{bottomlength} 
where id=#{id}
